INSERT INTO `products` (`id`, `brand_id`, `category_id`, `created_by`, `description`, `detail`, `image`, `name`, `presale`, `price`, `qty`, `slug`, `status`, `updated_by`) VALUES
(4, 1, 1, 0, 'MACBOOK PRO RETINA 2018 15 INCH TOUCHBAR\nPhiên bản Macbook Pro Retina 2018 15 inch được nâng cấp mạnh mẽ mang đến cho người dùng những trải nghiệm hiệu suất cao. Thiết kế bên ngoài vẫn tinh tế và sang trọng như trước đây cùng thời lượng pin 10 tiếng', 'CPU: Intel Core i7 2.6GHz RAM: 32GB Hard drive: 512GB SSD VGA: AMD Radeon™ PRO 560X with 4GB of GDDR5 Display: 15.4\" Retina Display with True Tone, Touch Bar, 500 nits Tình trạng: Used NoBox', 'dh.jpg', 'MACBOOK PRO 15 INCH - 2018', 110, 120, 2, 'product', 2, 1),
(5, 1, 1, 0, 'MacBook Pro M3 8 GB là một bước tiến đáng kể trong dòng sản phẩm laptop của Apple, nổi bật với sự tập trung tối ưu hóa hiệu suất và đồ họa đỉnh cao', 'MacBook Pro M3 8 GB là một bước tiến đáng kể trong dòng sản phẩm laptop của Apple, nổi bật với sự tập trung tối ưu hóa hiệu suất và đồ họa đỉnh cao. Với con chip M3 mạnh mẽ, sản phẩm này đặt ra một tiêu chuẩn mới cho hiệu năng và thiết kế thanh lịch. Điều này hứa hẹn mang đến trải nghiệm làm việc mượt mà và hiệu quả, đồng hành đỉnh cao cho tất cả tác vụ từ văn phòng, giải trí cho đến đồ họa chuyên nghiệp.', 'dh1.jpg', 'Asus ROG Zephyrus G14', 200, 124, 9, 'product', 3, 1),
(6, 1, 1, 0, 'Con chip Apple M3 Max là phiên bản vi xử lý tối tân nhất trong sự kiện ra mắt lần này của Apple, chip sở hữu đến 14 nhân CPU cùng 30 nhân GPU được thiết kế với tiến trình 3 nm hoàn toàn mới. ', 'Ghi dấu đậm nét vào những tháng cuối năm 2023 này, Apple đã tung ra thị trường thế hệ Macbook Pro M3 Max 14 inch 36 GB với sự góp mặt của con chip M3 Max hoàn toàn mới đi cùng những thông số cải tiến đầy ưu việt, hứa hẹn sẽ đáp ứng chuyên sâu nhất mọi công việc, những tác vụ phức tạp cho người dùng.', 'ln.jpg', 'Laptop Apple MacBook Pro 14 ', 390, 222, 9, 'product', 1, 2),
(7, 1, 1, 0, 'Khả năng xử lý đỉnh cao nhờ con chip tân tiến M3 Pro', 'Được trang bị bộ vi xử lý M3 Pro mạnh mẽ nhất từ trước đến nay của Apple, MacBook Pro M3 Pro 16 inch không chỉ đáp ứng mọi yêu cầu của người dùng tác vụ từ văn phòng, giải trí cho đến đồ họa chuyên nghiệp mà còn tạo nên một làn gió mới trong thế giới công nghệ. Sự kết hợp giữa thiết kế thanh lịch với hiệu năng vượt trội không chỉ là một xu hướng mà còn là sự thực hiện đẳng cấp khi lần đầu ra mắt, hứa hẹn góp mặt trong danh sách \"siêu phẩm\" laptop đồ họa - kỹ thuật hot nhất cuối năm 2023.', 'dh4.jpg', 'Laptop Apple MacBook Pro 16 ', 500, 440, 9, 'product', 1, 2),
(8, 1, 1, 0, 'CPU Apple M3 Pro với 11 nhân bên trong Macbook được xây dựng dựa trên tiến trình 3 nm tân tiến, khả năng băng thông bộ nhớ đạt tốc độ 150 GB/s cung cấp khả năng xử lý chuyên sâu với mọi tác ', 'MacBook Pro M3 Pro 1TB thể hiện kiểu dáng quen thuộc qua ngôn ngữ thiết kế đậm chất cao cấp, nay đã được cải tiến với hiệu năng ngoài mong đợi từ con chip Apple M3 Pro, xứng tầm cho mọi doanh nhân và đáp ứng \"cặn kẽ\" mọi yêu cầu cho những nhà sáng tạo nội dung chuyên nghiệp.', 'dh7.jpg', 'Laptop Apple MacBook Pro', 700, 520, 9, 'product', 1, 2),
(9, 1, 2, 0, 'CPU Apple M3 Pro với 11 nhân bên trong Macbook được xây dựng dựa trên tiến trình 3 nm tân tiến, khả năng băng thông bộ nhớ đạt tốc độ 150 GB/s cung cấp khả năng xử lý chuyên sâu với mọi tác ', 'Nhanh Chóng - Uy Tín - Trách Nhiệm trong suốt thời gian bảo hành. Mọi vấn đề của Macbook còn trong thời gian bảo hành đều được bảo hành miễn phí một cách nhanh chóng, để đảm bảo bạn có một trải nghiệm sử dụng mà không phải lo lắng về vấn đề gì khác', 'gm.jpg', 'Macbook Air Z15S00092', 400, 221, 9, 'product', 1, 2),
(10, 1, 1, 0, 'MacBook Pro 13 inch 2021 mang đến hiệu năng mạnh mẽ và thời lượng pin ấn tượng nhờ chip Apple M1.', 'MacBook Pro 13 inch 2021 với chip Apple M1, màn hình Retina và thời lượng pin lên đến 20 giờ.', 'gm_1.jpg', 'MacBook Pro 13 inch 2021', 1350, 1299, 10, 'macbook-pro-13-2021', 1, 2),
(11, 1, 1, 0, 'MacBook Air 13 inch 2020 với chip Apple M1 mang đến hiệu năng cao trong một thiết kế nhỏ gọn.', 'MacBook Air 13 inch 2020 với chip Apple M1, màn hình Retina và thiết kế mỏng nhẹ, phù hợp cho công việc hàng ngày.', 'gm_2.jpg', 'MacBook Air 13 inch 2020', 1050, 999, 8, 'macbook-air-13-2020', 1, 3),
(12, 1, 2, 0, 'MacBook Pro 14 inch 2021 mang đến hiệu năng cao và màn hình chất lượng cao trong một thiết kế nhỏ gọn.', 'MacBook Pro 14 inch 2021 với chip Apple M1 Pro, màn hình Liquid Retina XDR và thiết kế nhỏ gọn phù hợp cho người dùng chuyên nghiệp.', 'gm_3.jpg', 'MacBook Pro 14 inch 2021', 2100, 1999, 7, 'macbook-pro-14-2021', 1, 1),
(13, 1, 3, 0, 'MacBook Pro 15 inch 2019 với hiệu năng mạnh mẽ từ chip Intel Core i9 và màn hình Retina chất lượng cao.', 'MacBook Pro 15 inch 2019 với chip Intel Core i9, màn hình Retina và card đồ họa Radeon Pro 560X, lý tưởng cho các tác vụ nặng.', 'gm_4.jpg', 'MacBook Pro 15 inch 2019', 2300, 2200, 4, 'macbook-pro-15-2019', 1, 2),
(14, 1, 3, 0, 'MacBook Pro 13 inch 2020 mang đến hiệu năng ổn định với chip Intel Core i5 và bàn phím Magic Keyboard cải tiến.', 'MacBook Pro 13 inch 2020 với chip Intel Core i5, màn hình Retina và bàn phím Magic Keyboard mới, lý tưởng cho công việc hàng ngày.', 'gm1.jpg', 'MacBook Pro 13 inch 2020', 1600, 1500, 6, 'macbook-pro-13-2020', 1, 1),
(15, 1, 2, 0, 'MacBook Air 11 inch 2015 mang đến sự kết hợp hoàn hảo giữa tính di động và hiệu năng ổn định với chip Intel Core i5.', 'MacBook Air 11 inch 2015 với chip Intel Core i5, màn hình nhỏ gọn và thời lượng pin lên đến 9 giờ, lý tưởng cho di chuyển.', 'gm5.jpg', 'MacBook Air 11 inch 2015', 850, 800, 15, 'macbook-air-11-2015', 1, 3),
(16, 1, 2, 0, 'MacBook Pro 17 inch 2011 mang đến không gian làm việc rộng rãi và hiệu năng mạnh mẽ từ chip Intel Core i7.', 'MacBook Pro 17 inch 2011 với chip Intel Core i7, màn hình lớn và card đồ họa AMD Radeon, lý tưởng cho đồ họa và video.', 'gm6.jpg', 'MacBook Pro 17 inch 2011', 1600, 1500, 2, 'macbook-pro-17-2011', 1, 1),
(17, 1, 2, 0, 'MacBook Air 13 inch 2019 mang đến thiết kế mỏng nhẹ và hiệu năng ổn định với chip Intel Core i5.', 'MacBook Air 13 inch 2019 với chip Intel Core i5, màn hình Retina và bàn phím Butterfly, lý tưởng cho công việc và học tập.', 'gm7.jpg', 'MacBook Air 13 inch 2019', 1050, 999, 8, 'macbook-air-13-2019', 1, 2),
